import React from "react";
import SearchBox from "../components/search-box/search-box.component";
import CoachesData from "../components/coaches-data/coachesData";


import classes from '../styles/coaches.module.css'

const CoachesPage = () => {



    //turn coaches object into array of objects
    const coaches = Object.values(CoachesData);



    const [searchField, setSearchField] = useState('');

    // useEffect = (() => {

    // }, []) 


    const handleSearchChange = (event) =>{
       const searchFieldVal = event.target.value.toLoacaleLowerCase();
       return setSearchField(searchFieldVal)
    } 

    const filteredCoaches = coaches.filter((coach) =>{
        return coach.name.toLoacaleLowerCase().inlcudes(searchField)
    })









    //reutn a div for each coach with their image, name,title and short info
    return (
        <div className={classes.coaches_list}>   
          {/* <div>
             <SearchBox  coaches ={coaches}/>  
    </div>  */}

            {coaches.map(coach => (

                    <div className ={classes.coaches_card} key={coach.id}>
                        <img src={coach.image} alt="coache"/>
                        <h1>{coach.name}</h1>
                        <h3>{coach.title}</h3>
                        <p>{coach.shortInfo}</p> 
                    </div>
                )
            )}    
        </div>
    )
}

export default CoachesPage;
